mapper.select:
select distinct rm.menu_id from sys_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

select  from sys_user as su  where su.flag = 1 and su.type =1

	and su.user_id = #{userId}


	and su.email = #{email}

SELECT  from sys_user as su where su.flag = 1 and su.is_bind_mtype = 1

	and su.user_id = #{userId}


	and su.mobile = #{mobile}

select  from sys_user where flag = 1

	and parent_id = #{userId}
	or user_id = #{userId}

select  from sys_user where flag = 1

	and all_parent_id like concat(#{allParentId},',%')


	or user_id = #{userId}

select user_id from sys_user where flag=1

	and email=#{contact} or mobile =#{contact}

limit 1

select  from sys_user where flag=1

     and user_id in

		#{item}

select   from sys_user where flag=1

	and email = #{email}


	and user_id != #{userId}

select   from sys_user where flag=1

	and mobile = #{mobile}


	and user_id != #{userId}

select   from sys_user where flag=1

	and user_id = #{userId}

and (type = 1 or is_bind_mtype = 1)
